{
  "query": "query GetMedicalHistoryByPatientId($patientId: ID!) {
    getMedicalHistoryByPatientId(patientId: $patientId) {
      patient {
        id
        name
        cpf
        email
        dateOfBirth
      }
      appointments {
        id
        appointmentDateTime
        status
        doctor {
          id
          name
          specialty
        }
        actionLogs {
          actionType
          timestamp
          user {
            name
            role
          }
        }
      }
    }
  }",
  "variables": {
    "patientId": "{{patient_id}}"
  }
}

{
  "query": "query GetAppointmentById($appointmentId: ID!, $patientId: ID!) {
    getAppointmentById(appointmentId: $appointmentId, patientId: $patientId) {
      id
      appointmentDateTime
      status
      doctor {
        id
        name
        specialty
      }
    }
  }",
  "variables": {
    "appointmentId": "{{appointment_id}}",
    "patientId": "{{patient_id}}"
  }
}

{
  "query": "query {
    getMedicalHistoryByPatientId(patientId: "{{patient_id}}") {
      patient {
        name
      }
      appointments {
        id
        appointmentDateTime
        status
        doctor {
          name
          specialty
        }
      }
    }
  }"
}
