{
  "query": "query GetMedicalHistoryByPatientId($patientId: ID!) {
    getMedicalHistoryByPatientId(patientId: $patientId) {
      patient {
        id
        name
        cpf
        email
        dateOfBirth
      }
      appointments {
        id
        appointmentDateTime
        status
        doctor {
          id
          name
          specialty
        }
        actionLogs {
          actionType
          timestamp
          user {
            name
            role
          }
        }
      }
    }
  }",
  "variables": {
    "patientId": "{{paciente_id}}"
  }
}

{
  "query": "query GetAppointmentById($appointmentId: ID!, $patientId: ID!) {
    getAppointmentById(appointmentId: $appointmentId, patientId: $patientId) {
      id
      appointmentDateTime
      status
      doctor {
        id
        name
        specialty
      }
      actionLogs {
        actionType
        timestamp
        user {
          name
          role
        }
      }
    }
  }",
  "variables": {
    "appointmentId": "{{consulta_id}}",
    "patientId": "{{paciente_id}}"
  }
}

{
  "query": "query GetAllMedicalHistories {
    getAllMedicalHistories {
      patient {
        id
        name
        cpf
        email
        dateOfBirth
      }
      appointments {
        id
        appointmentDateTime
        status
        doctor {
          id
          name
          specialty
        }
      }
    }
  }"
}

{
  "query": "query IntrospectionQuery {
    __schema {
      queryType { name }
      mutationType { name }
      subscriptionType { name }
      types {
        ...FullType
      }
      directives {
        name
        description
        locations
        args {
          ...InputValue
        }
      }
    }
  }

  fragment FullType on __Type {
    kind
    name
    description
    fields(includeDeprecated: true) {
      name
      description
      args {
        ...InputValue
      }
      type {
        ...TypeRef
      }
      isDeprecated
      deprecationReason
    }
    inputFields {
      ...InputValue
    }
    interfaces {
      ...TypeRef
    }
    enumValues(includeDeprecated: true) {
      name
      description
      isDeprecated
      deprecationReason
    }
    possibleTypes {
      ...TypeRef
    }
  }

  fragment InputValue on __InputValue {
    name
    description
    type { ...TypeRef }
    defaultValue
  }

  fragment TypeRef on __Type {
    kind
    name
    ofType {
      kind
      name
      ofType {
        kind
        name
        ofType {
          kind
          name
          ofType {
            kind
            name
            ofType {
              kind
              name
              ofType {
                kind
                name
                ofType {
                  kind
                  name
                }
              }
            }
          }
        }
      }
    }
  }"
}
